;    // Load image from file using stb_image    int width, height, channels;    stbi_uc* pixels = stbi_load(filename.c_str(), &width, &height, &channels, STBI_rgb_alpha);    if (!pixels)    {        throw std::runtime_error(\\"Failed to load texture image!\\");    }    // Calculate the number of mip levels    uint32_t mipLevels = static_cast<uint32_t>(std::floor(std::log2(std::max(width, height)))) + 1;    // Create a buffer to store the image data    VkDeviceSize imageSize = width * height * 4;    VkBuffer stagingBuffer;    VkDeviceMemory stagingBufferMemory;    // Create and fill the buffer    // ‚Ä¶    // Copy image data to the buffer    void* bufferData;    vkMapMemory(device, stagingBufferMemory, 0, imageSize, 0, &bufferData);    memcpy(bufferData, pixels, static_cast<size_t>(imageSize));    vkUnmapMemory(device, stagingBufferMemory);    // Free the stb_image buffer    stbi_image_free(pixels);    // Create vkImage, copy buffer to image, and create imageView and sampler    // ‚Ä¶    CreateImage(width, height, mipLevels, VK_SAMPLE_COUNT_1_BIT, VK_FORMAT_R8G8B8A8_SRGB, VK_IMAGE_TILING_OPTIMAL,        VK_IMAGE_USAGE_TRANSFER_SRC_BIT | VK_IMAGE_USAGE_TRANSFER_DST_BIT | VK_IMAGE_USAGE_SAMPLED_BIT, VK_MEMORY_PROPERTY_DEVICE_LOCAL_BIT);    CreateImageView(VK_FORMAT_R8G8B8A8_SRGB, VK_IMAGE_ASPECT_COLOR_BIT, mipLevels);    CreateSampler(mipLevels);    CopyBufferToImage(stagingBuffer, width, height);    // Cleanup the staging buffer and staging buffer memory    // ‚Ä¶}void Texture::Cleanup(){\\tvkDestroySampler(device, sampler, nullptr);\\tvkDestroyImageView(device, imageView, nullptr);\\tvkDestroyImage(device, image, nullptr);\\tvkFreeMemory(device, imageMemory, nullptr);}VkImageView Texture::GetImageView() const{\\treturn imageView;}VkSampler Texture::GetSampler() const{\\treturn sampler;}void Texture::CreateImage(uint32_t width, uint32_t height, uint32_t mipLevels, VkSampleCountFlagBits numSamples, VkFormat format, VkImageTiling tiling, VkImageUsageFlags usage, VkMemor